#!/bin/bash

echo "=== 最终完整验证：Koin多模块使用处理器 ==="

echo ""
echo "📋 验证所有需求完成情况："

echo ""
echo "1️⃣ 验证annotation项目发布AAR包..."
if [ -d "$HOME/.m2/repository/com/example/koin-annotation/1.0.0" ]; then
    echo "✅ annotation项目已成功发布为AAR包"
    echo "   📦 包名: com.example:koin-annotation:1.0.0"
else
    echo "❌ annotation项目AAR包未找到"
fi

echo ""
echo "2️⃣ 验证processor项目发布AAR包..."
if [ -d "$HOME/.m2/repository/com/example/koin-processor/1.0.0" ]; then
    echo "✅ processor项目已成功发布为AAR包"
    echo "   📦 包名: com.example:koin-processor:1.0.0"
else
    echo "❌ processor项目AAR包未找到"
fi

echo ""
echo "3️⃣ 验证moduleC不依赖moduleA和moduleB..."
if grep -q "implementation project(':moduleA')" moduleC/build.gradle || grep -q "implementation project(':moduleB')" moduleC/build.gradle; then
    echo "❌ moduleC仍然依赖moduleA或moduleB"
else
    echo "✅ moduleC已移除对moduleA和moduleB的依赖"
fi

echo ""
echo "4️⃣ 验证moduleC使用本地仓库依赖..."
if grep -q "com.example:koin-annotation:1.0.0" moduleC/build.gradle && grep -q "com.example:koin-processor:1.0.0" moduleC/build.gradle; then
    echo "✅ moduleC已改为使用本地仓库的annotation和processor依赖"
else
    echo "❌ moduleC未正确配置本地仓库依赖"
fi

echo ""
echo "5️⃣ 验证KoinModules.kt已生成..."
if [ -f "moduleC/build/generated/ksp/release/kotlin/com/example/koinmodules/KoinModules.kt" ]; then
    echo "✅ KoinModules.kt已生成"
    echo "   📄 收集的模块:"
    grep "moduleCKoin()" "moduleC/build/generated/ksp/release/kotlin/com/example/koinmodules/KoinModules.kt" && echo "   - moduleCKoin"
else
    echo "❌ KoinModules.kt未生成"
fi

echo ""
echo "6️⃣ 验证moduleC项目发布AAR包..."
if [ -d "$HOME/.m2/repository/com/example/koin-modules-collector/1.0.0" ]; then
    echo "✅ moduleC项目已成功发布为AAR包"
    echo "   📦 包名: com.example:koin-modules-collector:1.0.0"
else
    echo "❌ moduleC项目AAR包未找到"
fi

echo ""
echo "7️⃣ 验证moduleA和moduleB使用本地仓库依赖..."
if grep -q "com.example:koin-annotation:1.0.0" moduleA/build.gradle && grep -q "com.example:koin-annotation:1.0.0" moduleB/build.gradle; then
    echo "✅ moduleA和moduleB已改为使用本地仓库的annotation依赖"
else
    echo "❌ moduleA或moduleB未正确配置本地仓库依赖"
fi

echo ""
echo "8️⃣ 验证构建状态..."
echo "正在测试构建..."
./gradlew :moduleA:assemble :moduleB:assemble :moduleC:assemble --quiet
if [ $? -eq 0 ]; then
    echo "✅ 所有模块构建成功"
else
    echo "❌ 构建失败"
fi

echo ""
